sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var updateCnt = 0;
	var fieldsUpdated = '';
	if (current.operation() == 'update') {
		// For an update only interested in url, mid server, integrtion user, polling interval, max retries
		// last_button_clicked
		for (var x in current){
			if (x == 'ucontrol_url' || x == 'ucontrol_mid_server' || x == 'ucontrol_integration_user' || x == 'ucontrol_polling_interval_minutes' || x == 'max_retries' || x == 'status' || x == 'connection_sttaus' || x == 'cmdb_tracking' || x == 'last_button_clicked') {
				if (current[x] != previous[x]) {
					if (updateCnt == 0) {
						fieldsUpdated = x;
					}
					else {
						fieldsUpdated = fieldsUpdated + ', ' + x;
					}
					updateCnt++;				
				} // if (current[x] != previous[x])
			}
		} // for
	} // if (current.operation() == 'update')

	if (current.operation() == 'insert' || current.operation() == 'delete' || updateCnt > 0) {
		// Only if insert, delete or an update (of a field we are interested in)
		var gr1 = new GlideRecord('x_tekso_twxapp01_setup_history');
		gr1.initialize();
		gr1.setValue('operation',current.operation());		
		gr1.setValue('ucontrol_url',current.ucontrol_url);
		gr1.setValue('ucontrol_mid_server_name',current.ucontrol_mid_server.name);
		gr1.setValue('ucontrol_mid_server_sys_id',current.ucontrol_mid_server.sys_id);
		gr1.setValue('ucontrol_integration_user_name',current.ucontrol_integration_user.name);
		gr1.setValue('ucontrol_integration_user_sys_id',current.ucontrol_integration_user.sys_id);
		gr1.setValue('ucontrol_polling_interval_minutes',current.ucontrol_polling_interval_minutes);
		gr1.setValue('max_retries',current.max_retries);
		gr1.setValue('status',current.status);
		gr1.setValue('connection_status',current.connection_status);
		gr1.setValue('cmdb_tracking',current.cmdb_tracking);	
		gr1.setValue('last_button_clicked',current.last_button_clicked);
		if (updateCnt > 0) {
			// For an update - capture the previous values as well
			gr1.setValue('previous_ucontrol_url',previous.ucontrol_url);
			gr1.setValue('previous_ucontrol_mid_server_name',previous.ucontrol_mid_server.name);
			gr1.setValue('previous_ucontrol_mid_server_sys_id',previous.ucontrol_mid_server.sys_id);
			gr1.setValue('previous_ucontrol_integration_user_name',previous.ucontrol_integration_user.name);
			gr1.setValue('previous_ucontrol_integration_user_sys_id',previous.ucontrol_integration_user.sys_id);
			gr1.setValue('previous_ucontrol_polling_interval_minutes',previous.ucontrol_polling_interval_minutes);
			gr1.setValue('previous_max_retries',previous.max_retries);
			gr1.setValue('previous_status',previous.status);
			gr1.setValue('previous_connection_status',previous.connection_status);
			gr1.setValue('previous_cmdb_tracking',previous.cmdb_tracking);	
			gr1.setValue('previous_last_button_clicked',previous.last_button_clicked);
			gr1.setValue('fields_updated',fieldsUpdated); 
		}
		gr1.insert();
	}
})(current, previous);
