sys_script.script:
//After Insert/Update
(function executeRule(current, previous) {

	// IF this is 0 then we don't have good connection details
	// so no point even bothering to try testing the connection
	var gotConnectionData = 1;

	var midServerName = current.ucontrol_mid_server.name.toString();
	var midServerSysID = current.ucontrol_mid_server;

	if (midServerName == '' || midServerName.length < 1) {
		gotConnectionData = 0;
	}

	if (midServerSysID == '' || midServerSysID.length < 1) {
		gotConnectionData = 0;
	}

	var url = current.ucontrol_url.toString();  

	if (url == '' || url.length < 1) {
		gotConnectionData = 0;
	}

	if (gotConnectionData == 1) {
		// Got a MID Server and a URL

		var dtRequest = new GlideDateTime();

		// gets the user name
		var userName = gs.getUserName();

		// e.g. dev91664
		var snInstance = gs.getProperty("instance_name");	
		// e.g. https://dev91664.service-now.com/
		var snURL = gs.getProperty("glide.servlet.uri");

		var source = 'ServiceNow';

		var json = '{\"source\":\"' + source + '\",';	// ServiceNow
		json = json  + '\"source_name\":\"' + snInstance + '\",'; // ServiceNow Instance e.g. dev91664
		json = json + '\"source_url\":\"' + snURL + '\",'; // ServiceNow URL e.g. https://dev91664.service-now.com/
		json = json +  '\"date_sent\":\"' + dtRequest + '\",';	
		json = json + '\"user\":\"' + userName + '\"}'; // No comma at the end	

		var connectionTestTable = 'x_tekso_twxapp01_connection_test';
		var gr1 = new GlideRecord(connectionTestTable);
		gr1.initialize();
		gr1.setValue('setup_record',current.sys_id);
		gr1.setValue('ucontrol_url',url);
		gr1.setValue('ucontrol_mid_server_name',midServerName);
		gr1.setValue('ucontrol_mid_server_sys_id',midServerSysID);	
		gr1.setValue('json_request_body',json);
		gr1.setValue('test_connection_triggered_by','BR Check Connection Status If Changed');

		if (current.start_sending_requested == true) {
			gr1.setValue('set_status_to_running_if_test_successful',true);				
		}
		else {
			gr1.setValue('set_status_to_running_if_test_successful',false);
		}

		var connectionTestSysID = gr1.insert();

		var si = new x_tekso_twxapp01.OutboundREST();
		si.processTestConnection(connectionTestSysID);

	}

})(current, previous);
