sys_script_client.script:
function onLoad() {

	// We use different views for New + unSubmitted to hide a lot of the fields
	var stat = g_form.getValue('status');
	var delCI = g_form.getValue('delete_cmdb_ci_qty');
	var updCI = g_form.getValue('update_cmdb_ci_qty');
	var delRel = g_form.getValue('delete_relations_qty');
	var updRel = g_form.getValue('update_relations_qty');
	var updSrv = g_form.getValue('update_server_ci_qty');
	var updApp = g_form.getValue('update_application_ci_qty');

	var fields = g_form.getEditableFields();
	for (var x = 0; x < fields.length; x++) {
		// Hide all fields by default
		g_form.setDisplay(fields[x], false);
		// Set all the fields to readonly by default
		g_form.setReadOnly(fields[x], true);		
	}

	var writeFldsForNewArr = ['delete_cmdb_ci_qty','update_cmdb_ci_qty','delete_relations_qty','update_relations_qty'];	

	var visibleFldsForNewArr = ['delete_cmdb_ci_qty','update_cmdb_ci_qty','delete_relations_qty','update_relations_qty'];	
	visibleFldsForNewArr.push('status');
	visibleFldsForNewArr.push('available_qty_of_test_cmdb_cis');
	visibleFldsForNewArr.push('available_qty_of_test_relations');

	var visibleFldsForAfterNewArr = ['delete_cmdb_ci_qty','update_cmdb_ci_qty','delete_relations_qty','update_relations_qty'];
	visibleFldsForAfterNewArr.push('status');
	visibleFldsForAfterNewArr.push('available_qty_of_test_cmdb_cis');
	visibleFldsForAfterNewArr.push('available_qty_of_test_relations');	
	visibleFldsForAfterNewArr.push('build_test_data_started');
	visibleFldsForAfterNewArr.push('build_test_data_started_at');
	visibleFldsForAfterNewArr.push('build_test_data_completed');
	visibleFldsForAfterNewArr.push('build_test_data_completed_at');	
	visibleFldsForAfterNewArr.push('time_taken_to_build_test_data');
	if (delCI > 0) {
		visibleFldsForAfterNewArr.push('build_delete_cmdb_ci_test_data_started');
		visibleFldsForAfterNewArr.push('build_delete_cmdb_ci_test_data_completed');
		visibleFldsForAfterNewArr.push('build_delete_cmdb_ci_test_data_started_at');
		visibleFldsForAfterNewArr.push('build_delete_cmdb_ci_test_data_completed_at');		
	}

	if (delRel > 0) {
		visibleFldsForAfterNewArr.push('build_delete_relations_test_data_started');
		visibleFldsForAfterNewArr.push('build_delete_relations_test_data_completed');
		visibleFldsForAfterNewArr.push('build_delete_relations_test_data_started_at');
		visibleFldsForAfterNewArr.push('build_delete_relations_test_data_completed_at');		
	}	

	if (updRel > 0) {
		visibleFldsForAfterNewArr.push('build_update_relations_test_data_started');
		visibleFldsForAfterNewArr.push('build_update_relations_test_data_completed');
		visibleFldsForAfterNewArr.push('build_update_relations_test_data_started_at');
		visibleFldsForAfterNewArr.push('build_update_relations_test_data_completed_at');		
	}

	if (updCI > 0 || updSrv > 0) {
		visibleFldsForAfterNewArr.push('build_update_server_ci_test_data_started');
		visibleFldsForAfterNewArr.push('build_update_server_ci_test_data_completed');
		visibleFldsForAfterNewArr.push('build_update_server_ci_test_data_started_at');
		visibleFldsForAfterNewArr.push('build_update_server_ci_test_data_completed_at');		
		visibleFldsForAfterNewArr.push('update_server_ci_qty');
	}

	if (updCI > 1 || updApp > 0) {
		visibleFldsForAfterNewArr.push('build_update_application_ci_test_data_started');
		visibleFldsForAfterNewArr.push('build_update_application_ci_test_data_completed');
		visibleFldsForAfterNewArr.push('build_update_application_ci_test_data_started_at');
		visibleFldsForAfterNewArr.push('build_update_application_ci_test_data_completed_at');		
		visibleFldsForAfterNewArr.push('update_application_ci_qty');	
	}

	for (var x2 = 0; x2 < fields.length; x2++) {
		if (stat == 'New' || stat == 'Unsubmitted') {
			if (visibleFldsForNewArr.indexOf(fields[x2])> -1) {
				g_form.setDisplay(fields[x2], true);
			}

			// Set the fields readonly = false, i.e writeable if in this list		
			if (writeFldsForNewArr.indexOf(fields[x2]) > -1) {
				g_form.setReadOnly(fields[x2], false);
			}
		}
		else {			
			if (visibleFldsForAfterNewArr.indexOf(fields[x2]) > -1) {
				g_form.setDisplay(fields[x2], true);
			}
		}
	}
}
