sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading) {
      return;
   }

	if (newValue < 0) {
		g_form.showFieldMsg('delete_cmdb_ci_qty','Value cannot be negative');	
	}
	else{
		var avail = g_form.getValue('available_qty_of_test_cmdb_cis');
		var other = g_form.getValue('update_cmdb_ci_qty');
		// Remove commas
		avail = avail.replace(/,/g, "");
		other = other.replace(/,/g, "");
		newValue = newValue.replace(/,/g, "");
		var total = parseInt(other) + parseInt(newValue);
		if (parseInt(total) > parseInt(avail)) {
			g_form.showFieldMsg('delete_cmdb_ci_qty','Delete CMDB CI Qty and Update CMDB CI Qty combined cannot be more than ' + avail.toString());	
		}
		else {
			g_form.hideFieldMsg('delete_cmdb_ci_qty');					
		}
	}
}
