sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading) {
      return;
   }

	var noOfFieldsModified = 0;
	for(var i in g_form.modifiedFields) {
		var n = i.indexOf("number_of_pending_records");
		if (n < 0) {
			// Field modified is not number_of_pending_records
			// Only increment noOfFieldsModified if the field is not number_of_pending_records
			noOfFieldsModified++;
		}
	}
	/*
	alert(noOfFieldsModified.toString());
	if (noOfFieldsModified > 0) {
		g_form.modified = true;
	}
	else {
		// This is the only field on the form that has been modified
		g_form.modified = false;			
	}
	alert(g_form.modified);
	*/
}
