sys_script.script:
// Async
(function executeRule(current, previous /*null when async*/) {

	var delete_cmdb_ci_qty = current.delete_cmdb_ci_qty;
	var delete_relations_qty = current.delete_relations_qty;
	var update_relations_qty = current.update_relations_qty;
	var update_cmdb_ci_qty = current.update_cmdb_ci_qty;

	// Restore any deleted items
	new x_tekso_twxapp01.SoftDeleteOfTestCIsAndRels().restoreSoftDeletesSilently();

	// Restore any updated items
	new x_tekso_twxapp01.UpdateOfTestRels().restoreAllCMDBRelationRecordsSilently();

	new x_tekso_twxapp01.UpdateOfTestCMDBCIs().restoreAllCMDBCIRecordsSilently();

	if (delete_cmdb_ci_qty > 0) {
		// Cap at 100
		if (delete_cmdb_ci_qty > 100) {
			delete_cmdb_ci_qty = 100;
		}
		new x_tekso_twxapp01.SoftDeleteOfTestCIsAndRels().softDeleteCIs(delete_cmdb_ci_qty);
	}

	if (delete_relations_qty > 0) {
		// Cap at 100
		if (delete_relations_qty > 100) {
			delete_relations_qty = 100;
		}		
		new x_tekso_twxapp01.SoftDeleteOfTestCIsAndRels().softDeleteCIRelationships(delete_relations_qty);		
	}

	if (update_relations_qty > 0) {
		// Cap at 100
		if (update_relations_qty > 100) {
			update_relations_qty = 100;
		}
		new x_tekso_twxapp01.UpdateOfTestRels().updateCMDBRelationRecords(update_relations_qty);		
	}

	if (update_cmdb_ci_qty > 0) {
		if (update_cmdb_ci_qty == 1) {
			// Just 1 record
			new x_tekso_twxapp01.UpdateOfTestCMDBCIs().updateCMDBServerRecords(1);				
		}
		else {
			// Cap at 100
			if (update_cmdb_ci_qty > 100) {
				update_cmdb_ci_qty = 100;
			}

			// Divide the qty by 2 = split the updates between servers and applications
			var qty = update_cmdb_ci_qty;
			var qty2 = qty / 2;  // e.g. 1.5
			var qty3 = parseInt(qty2); // convert to int e.g. 1
			var qty4 = qty - qty3;	// e.g. 3 - 1 = 2		
			new x_tekso_twxapp01.UpdateOfTestCMDBCIs().updateCMDBAppplicationRecords(qty4);
			new x_tekso_twxapp01.UpdateOfTestCMDBCIs().updateCMDBServerRecords(qty3);
		}	
	}

})(current, previous);
