sys_script.script:
// Async
(function executeRule(current, previous /*null when async*/) {

	var delete_cmdb_ci_qty = current.delete_cmdb_ci_qty;
	var delete_relations_qty = current.delete_relations_qty;
	var update_relations_qty = current.update_relations_qty;
	var update_cmdb_ci_qty = current.update_cmdb_ci_qty;
	var sys_id = current.sys_id;
	var genTable = 'x_tekso_twxapp01_generate_test_data';

	// Restore any deleted items
	new x_tekso_twxapp01.SoftDeleteOfTestCIsAndRels().restoreSoftDeletesSilently();

	// Restore any updated items
	new x_tekso_twxapp01.UpdateOfTestRels().restoreAllCMDBRelationRecordsSilently();

	new x_tekso_twxapp01.UpdateOfTestCMDBCIs().restoreAllCMDBCIRecordsSilently();

	// Delete CI's
	if (delete_cmdb_ci_qty > 0) {
		// Cap at 100
		if (delete_cmdb_ci_qty > 100) {
			delete_cmdb_ci_qty = 100;
		}
		new x_tekso_twxapp01.SoftDeleteOfTestCIsAndRels().softDeleteCIs(delete_cmdb_ci_qty,current.sys_id);
	}

	// Delete Rels
	if (delete_relations_qty > 0) {
		// Cap at 100
		if (delete_relations_qty > 100) {
			delete_relations_qty = 100;
		}		
		new x_tekso_twxapp01.SoftDeleteOfTestCIsAndRels().softDeleteCIRelationships(delete_relations_qty,current.sys_id);		
	}

	//Update Rels
	if (update_relations_qty > 0) {
		// Cap at 100
		if (update_relations_qty > 100) {
			update_relations_qty = 100;
		}
		new x_tekso_twxapp01.UpdateOfTestRels().updateCMDBRelationRecords(update_relations_qty,current.sys_id);		
	}

	// Update CI's - do a mix of servers and apps
	if (update_cmdb_ci_qty > 0) {
		var serverQty = 0;
		var applicationQty = 0;
		if (update_cmdb_ci_qty == 1) {
			// Just 1 record
			serverQty = 1;
			new x_tekso_twxapp01.UpdateOfTestCMDBCIs().updateCMDBServerRecords(serverQty,current.sys_id);				
		}
		else {
			// Cap at 100
			if (update_cmdb_ci_qty > 100) {
				update_cmdb_ci_qty = 100;
			}

			// Divide the qty by 2 = split the updates between servers and applications
			var qty = update_cmdb_ci_qty;
			var qty2 = qty / 2;  // e.g. 1.5
			serverQty = parseInt(qty2); // convert to int e.g. 1
			applicationQty = qty - serverQty;	// e.g. 3 - 1 = 2		
			new x_tekso_twxapp01.UpdateOfTestCMDBCIs().updateCMDBAppplicationRecords(applicationQty,current.sys_id);
			new x_tekso_twxapp01.UpdateOfTestCMDBCIs().updateCMDBServerRecords(serverQty,current.sys_id);
		}	

		var gr1 = new GlideRecord(genTable);
		gr1.addQuery('sys_id',sys_id);
		gr1.query();
		if (gr1.next()) {
			gr1.setValue('update_server_ci_qty',serverQty);
			gr1.setValue('update_application_ci_qty',applicationQty);
			gr1.setWorkflow(false);
			gr1.update();
		}
	}

})(current, previous);
