sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	//Test Data Build In Progress = is set by UI Action - Save & Build Test Data
	//build_test_data_started is set to true by SI Generate Data
	//number_of_build_test_data_options_started is incremeneted by SI GenerateData
	//
	// current.build_test_data_started != true && current.number_of_build_test_data_options_started < 1
	// is a safety check to stop the BR being triggered again
	// current.build_test_data_started starts as false and
	// current.number_of_build_test_data_options_started starts as 0
	// they are both set by SI GenerateData - which is called by this BR
	// Therefore if current.build_test_data_started is true or 
	// current.number_of_build_test_data_options_started is > 0 then this means this must have already
	// been triggered - so don't run again

	var sys_id = current.sys_id;
	var genTable = 'x_tekso_twxapp01_generate_test_data';

	var del_srv_qty = current.delete_server_qty;			
	var del_appl_qty = current.delete_application_qty;	
	var del_rel_qty = current.delete_relations_qty;	
	var upd_srv_qty = current.update_server_qty;		
	var upd_appl_qty = current.update_application_qty;			
	var upd_rel_qty = current.update_relations_qty;		
	var upd_srv_inc = current.update_server_include_all_fields;
	var upd_srv_fill = current.update_server_completely_fill_string_fields;
	var upd_appl_inc = current.update_application_include_all_fields;
	var upd_appl_fill = current.update_application_completely_fill_string_fields;

	var includeAllFields = false;
	var completelyFillStrings = false;
	var data = '';
	var qty = 0;

	if (del_srv_qty < 0) {
		del_srv_qty = 0;
	}

	if (del_appl_qty < 0) {
		del_appl_qty = 0;
	}

	if (del_rel_qty < 0) {
		del_rel_qty = 0;
	}

	if (upd_srv_qty < 0) {
		upd_srv_qty = 0;
	}

	if (upd_appl_qty < 0) {
		upd_appl_qty = 0;
	}

	if (upd_rel_qty < 0) {
		upd_rel_qty = 0;
	}

	if (del_srv_qty > 0) {
		// Cap at 2000
		if (del_srv_qty > 2000) {
			del_srv_qty = 2000;
		}
		new x_tekso_twxapp01.GenerateTestData().generate_data(del_srv_qty,sys_id,'delete_cmdb_ci_server',true,true);	
	}

	if (del_appl_qty > 0) {
		// Cap at 2000
		if (del_appl_qty > 2000) {
			del_appl_qty = 2000;
		}
		new x_tekso_twxapp01.GenerateTestData().generate_data(del_appl_qty,sys_id,'delete_cmdb_ci_appl',true,true);	
	}

	if (del_rel_qty > 0) {
		// Cap at 2000
		if (del_rel_qty > 2000) {
			del_rel_qty = 2000;
		}
		new x_tekso_twxapp01.GenerateTestData().generate_data(del_rel_qty,sys_id,'delete_cmdb_rel_ci',true,true);	
	}

	if (upd_srv_qty > 0) {
		// Cap at 2000
		if (upd_srv_qty > 2000) {
			upd_srv_qty = 2000;
		}
		new x_tekso_twxapp01.GenerateTestData().generate_data(upd_srv_qty,sys_id,'update_cmdb_ci_server',upd_srv_inc,upd_srv_fill);	
	}

	if (upd_appl_qty > 0) {
		// Cap at 10,000
		if (upd_appl_qty > 2000) {
			upd_appl_qty = 2000;
		}
		new x_tekso_twxapp01.GenerateTestData().generate_data(del_appl_qty,sys_id,'update_cmdb_ci_appl',upd_appl_inc,upd_appl_fill);	
	}

	if (upd_rel_qty > 0) {
		// Cap at 10,000
		if (upd_rel_qty > 2000) {
			upd_rel_qty = 2000;
		}
		new x_tekso_twxapp01.GenerateTestData().generate_data(upd_rel_qty,sys_id,'update_cmdb_rel_ci',true,true);	
	}	
	// Finished	
})(current, previous);
