	findOrInsertTestRelType: function (parent,child,name) {
		var rtnSysID = '';
		relTable = 'x_tekso_twxapp01_test_ci_relationship_type';
		var gr1 = new GlideRecord(relTable);
		gr1.addQuery('parent_descriptor','Runs on');
		gr1.addQuery('child_descriptor','Runs');
		gr1.addQuery('name','Runs on::Runs');
		gr1.query();
		if (!gr1.next()) {		
			// Does not exist
			var gr2 = new GlideRecord(relTable);
			gr2.initialize();
			gr2.setValue('parent_descriptor','Runs on');
			gr2.setValue('child_descriptor','Runs');
			rtnSysID = gr2.insert();	
		}
		else {
			rtnSysID = gr1.getValue('sys_id').toString();
		}
		return rtnSysID;
	},

	updateCMDBCIRecords: function (qty) {
		var updateSilently = false;
		var ciTable = 'x_tekso_twxapp01_test_configuration_item';

		var gr1 = new GlideRecord(ciTable);
		if (qty > 0) {
			gr1.setLimit(qty);
		}
		gr1.addQuery('original_or_alternative','original');
		gr1.query();
		while(gr1.next()) {
			var sysID = gr1.getValue('sys_id');
			var noRestore = false;
			this.updateCMDBCI(sysID,noRestore,updateSilently);	
		}
	},

	restoreAllCMDBCIRecordsSilently: function () {
		var updateSilently = true;
		var ciTable = 'x_tekso_twxapp01_test_configuration_item';

		var gr1 = new GlideRecord(ciTable);
		gr1.addQuery('original_or_alternative','alternative');
		gr1.query();	
		while(gr1.next()) {
			var sysID = gr1.getValue('sys_id');
			var restore = true;
			this.updateCMDBCI(sysID,restore,updateSilently);	
		}	
	},

	updateCMDBAppplicationRecords: function (qty) {
		var updateSilently = false;
