	updateCMDBCI: function (sys_id,restore,updateSilently) {
		var ciTable = 'x_tekso_twxapp01_test_configuration_item';

		var gr1 = new GlideRecord(ciTable);
		gr1.addQuery('sys_id',sys_id);
		gr1.query();
		if (gr1.next()) {
			var originalOrAlternative = gr1.getValue('original_or_alternative').toString();
			var gotAlternativeValues = gr1.getValue('got_alternative_values') || false;

			// restore = true to ensure that it goes here for a restore
			if (originalOrAlternative == 'Alternative' || restore == true) {

				// Get Original
				var original_value_name = gr1.getValue('original_value_name'); // 255
				var original_value_discovery_source = gr1.getValue('original_value_discovery_source'); // Choice ImportSet, Manual Entry, ServiceNow, TekWurx uControl
				var original_value_install_status = gr1.getValue('original_value_install_status'); // Choice Installed: 1, Absent: 100
				var original_value_operational_status = gr1.getValue('original_value_operational_status'); // Choice Operational: 1, Non-Operational: 2, Repair in Progress: 3

				// String
				var original_value_asset_tag = gr1.getValue('original_value_asset_tag'); // 40
				var original_value_attributes = gr1.getValue('original_value_attributes'); // 65000
				var original_value_category = gr1.getValue('original_value_category'); // 40
				var original_value_comments = gr1.getValue('original_value_comments'); // 4000
				var original_value_correlation_id = gr1.getValue('original_value_correlation_id'); // 512		
				var original_value_dns_domain = gr1.getValue('original_value_dns_domain'); // 255
				var original_value_fqdn = gr1.getValue('original_value_fqdn'); // 255
				var original_value_gl_account = gr1.getValue('original_value_gl_account'); // 40
				var original_value_invoice_number = gr1.getValue('original_value_invoice_number'); // 40
				var original_value_ip_address = gr1.getValue('original_value_ip_address'); // 255
				var original_value_lease_id = gr1.getValue('original_value_lease_id'); // 40
				var original_value_mac_address = gr1.getValue('original_value_mac_address'); // 18
				var original_value_model_number = gr1.getValue('original_value_model_number'); // 255
				var original_value_po_number = gr1.getValue('original_value_po_number'); // 40
				var original_value_serial_number = gr1.getValue('original_value_serial_number'); // 255
				var original_value_short_description = gr1.getValue('original_value_short_description'); // 1000		
				var original_value_subcategory = gr1.getValue('original_value_subcategory');

				// References
				var original_value_asset = gr1.getValue('original_value_asset'); // Asset

				var original_value_assigned_to = gr1.getValue('original_value_assigned_to'); //User
				var original_value_managed_by = gr1.getValue('original_value_managed_by');
				var original_value_owned_by = gr1.getValue('original_value_owned_by');
				var original_value_supported_by = gr1.getValue('original_value_supported_by');

				var original_value_assignment_group = gr1.getValue('original_value_assignment_group'); //Group
				var original_value_change_control = gr1.getValue('original_value_change_control');
				var original_value_support_group = gr1.getValue('original_value_support_group');

				var original_value_company = gr1.getValue('original_value_company'); //Company
				var original_value_manufacturer = gr1.getValue('original_value_manufacturer');
				var original_value_vendor = gr1.getValue('original_value_vendor');

				var original_value_model_id = gr1.getValue('original_value_model_id'); //Product Model

				var original_value_cost_center = gr1.getValue('original_value_cost_center'); //Cost Center

				var original_value_department = gr1.getValue('original_value_department'); //Department

				//duplicate_of //cmdb_ci

				var original_value_location = gr1.getValue('original_value_location'); //location

				var original_value_maintenance_schedule = gr1.getValue('original_value_maintenance_schedule'); //schedule
				var original_value_schedule = gr1.getValue('original_value_schedule');  

				// DateTime
				var original_value_assigned = gr1.getValue('original_value_assigned');
				var original_value_checked_in = gr1.getValue('original_value_checked_in');
				var original_value_checked_out = gr1.getValue('original_value_checked_out');
				var original_value_delivery_date = gr1.getValue('original_value_delivery_date');
				var original_value_due = gr1.getValue('original_value_due');
				var original_value_first_discovered = gr1.getValue('original_value_first_discovered');
				var original_value_install_date = gr1.getValue('original_value_install_date');
				var original_value_last_discovered = gr1.getValue('original_value_last_discovered');
				var original_value_order_date = gr1.getValue('original_value_order_date');
				var original_value_start_date = gr1.getValue('original_value_start_date');

				// Date		
				var original_value_warranty_expiration = gr1.getValue('original_value_warranty_expiration');
				var original_value_purchase_date = gr1.getValue('original_value_purchase_date');

				// True/False
				var original_value_can_print = gr1.getValue('original_value_can_print');
				var original_value_monitor = gr1.getValue('original_value_monitor');
				var original_value_skip_sync = gr1.getValue('original_value_skip_sync');			
				var original_value_unverified = gr1.getValue('original_value_unverified');

				//Integer
				var original_value_fault_count = gr1.getValue('original_value_fault_count');

				//Float
				var original_value_cost = gr1.getValue('original_value_cost');

				//Choice
				var original_value_justifcation = gr1.getValue('original_value_justification'); //Replacement, Testing, Upgrade
				var original_value_due_in = gr1.getValue('original_value_due_in'); //1 Hour, 1 Day, 1 Week
				var original_value_cost_cc = gr1.getValue('original_value_cost_cc'); // USD, EUR, GBP

				//Update
				gr1.setValue('name',original_value_name); // 255
				gr1.setValue('discovery_source',original_value_discovery_source); // Choice ImportSet, Manual Entry, ServiceNow, TekWurx uControl
				gr1.setValue('install_status',original_value_install_status); // Choice Installed: 1, Absent: 100
				gr1.setValue('operational_status',original_value_operational_status); // Choice Operational: 1, Non-Operational: 2, Repair in Progress: 3

				// String
				gr1.setValue('asset_tag',original_value_asset_tag); // 40
				gr1.setValue('attributes',original_value_attributes); // 65000
				gr1.setValue('category',original_value_category); // 40
				gr1.setValue('comments',original_value_comments); // 4000
				gr1.setValue('correlation_id',original_value_correlation_id); // 512		
				gr1.setValue('dns_domain',original_value_dns_domain); // 255
				gr1.setValue('fqdn',original_value_fqdn); // 255
				gr1.setValue('gl_account',original_value_gl_account); // 40
				gr1.setValue('invoice_number',original_value_invoice_number); // 40
				gr1.setValue('ip_address',original_value_ip_address); // 255
				gr1.setValue('lease_id',original_value_lease_id); // 40
				gr1.setValue('mac_address',original_value_mac_address); // 18
				gr1.setValue('model_number',original_value_model_number); // 255
				gr1.setValue('po_number',original_value_po_number); // 40
				gr1.setValue('serial_number',original_value_serial_number); // 255
				gr1.setValue('short_description',original_value_short_description); // 1000		
				gr1.setValue('subcategory',original_value_subcategory);

				// References
				gr1.setValue('asset',original_value_asset); // Asset

				gr1.setValue('assigned_to',original_value_assigned_to); //User
				gr1.setValue('managed_by',original_value_managed_by);
				gr1.setValue('owned_by',original_value_owned_by);
				gr1.setValue('supported_by',original_value_supported_by);

				gr1.setValue('assignment_group',original_value_assignment_group); //Group
				gr1.setValue('change_control',original_value_change_control);
				gr1.setValue('support_group',original_value_support_group);

				gr1.setValue('company',original_value_company); //Company
				gr1.setValue('manufacturer',original_value_manufacturer);
				gr1.setValue('vendor',original_value_vendor);

				gr1.setValue('model_id',original_value_model_id); //Product Model

				gr1.setValue('cost_center',original_value_cost_center); //Cost Center

				gr1.setValue('department',original_value_department); //Department

				//duplicate_of //cmdb_ci

				gr1.setValue('location',original_value_location); //location

				gr1.setValue('maintenance_schedule',original_value_maintenance_schedule); //schedule
				gr1.setValue('schedule',original_value_schedule);  

				// DateTime
				gr1.setValue('assigned',original_value_assigned);
				gr1.setValue('checked_in',original_value_checked_in);
				gr1.setValue('checked_out',original_value_checked_out);
				gr1.setValue('delivery_date',original_value_delivery_date);
				gr1.setValue('due',original_value_due);
				gr1.setValue('first_discovered',original_value_first_discovered);
				gr1.setValue('install_date',original_value_install_date);
				gr1.setValue('last_discovered',original_value_last_discovered);
				gr1.setValue('order_date',original_value_order_date);
				gr1.setValue('start_date',original_value_start_date);

				// Date		
				gr1.setValue('warranty_expiration',original_value_warranty_expiration);
				gr1.setValue('purchase_date',original_value_purchase_date);

				// True/False
				gr1.setValue('can_print',original_value_can_print);
				gr1.setValue('monitor',original_value_monitor);
				gr1.setValue('skip_sync',original_value_skip_sync);			
				gr1.setValue('unverified',original_value_unverified);

				//Integer
				gr1.setValue('fault_count',original_value_fault_count);

				//Float
				gr1.setValue('cost',original_value_cost);

				//Choice
				gr1.setValue('justification',original_value_justifcation); //Replacement, Testing, Upgrade
				gr1.setValue('due_in',original_value_due_in); //1 Hour, 1 Day, 1 Week
				gr1.setValue('cost_cc',original_value_cost_cc); // USD, EUR, GBP

				gr1.setValue('original_or_alternative','Original');

			}
			else {
				// Original
				if (gotAlternativeValues == true) {

					// Get Alternative
					var alternative_value_name = gr1.getValue('alternative_value_name'); // 255
					var alternative_value_discovery_source = gr1.getValue('alternative_value_discovery_source'); // Choice ImportSet, Manual Entry, ServiceNow, TekWurx uControl
					var alternative_value_install_status = gr1.getValue('alternative_value_install_status'); // Choice Installed: 1, Absent: 100
					var alternative_value_operational_status = gr1.getValue('alternative_value_operational_status'); // Choice Operational: 1, Non-Operational: 2, Repair in Progress: 3

					// String
					var alternative_value_asset_tag = gr1.getValue('alternative_value_asset_tag'); // 40
					var alternative_value_attributes = gr1.getValue('alternative_value_attributes'); // 65000
					var alternative_value_category = gr1.getValue('alternative_value_category'); // 40
					var alternative_value_comments = gr1.getValue('alternative_value_comments'); // 4000
					var alternative_value_correlation_id = gr1.getValue('alternative_value_correlation_id'); // 512		
					var alternative_value_dns_domain = gr1.getValue('alternative_value_dns_domain'); // 255
					var alternative_value_fqdn = gr1.getValue('alternative_value_fqdn'); // 255
					var alternative_value_gl_account = gr1.getValue('alternative_value_gl_account'); // 40
					var alternative_value_invoice_number = gr1.getValue('alternative_value_invoice_number'); // 40
					var alternative_value_ip_address = gr1.getValue('alternative_value_ip_address'); // 255
					var alternative_value_lease_id = gr1.getValue('alternative_value_lease_id'); // 40
					var alternative_value_mac_address = gr1.getValue('alternative_value_mac_address'); // 18
					var alternative_value_model_number = gr1.getValue('alternative_value_model_number'); // 255
					var alternative_value_po_number = gr1.getValue('alternative_value_po_number'); // 40
					var alternative_value_serial_number = gr1.getValue('alternative_value_serial_number'); // 255
					var alternative_value_short_description = gr1.getValue('alternative_value_short_description'); // 1000		
					var alternative_value_subcategory = gr1.getValue('alternative_value_subcategory');

					// References
					var alternative_value_asset = gr1.getValue('alternative_value_asset'); // Asset

