					var alternative_value2_config_directory = this.reverse(strConfigDirectory);

					var strConfigFile = gr1.getValue('config_file') || '';
					var alternative_value2_config_file = this.reverse(strConfigFile);

					var strEdition = gr1.getValue('edition') || '';
					var alternative_value2_edition = this.reverse(strEdition);

					var strInstallDirectory = gr1.getValue('install_directory') || '';
					var alternative_value2_install_directory = this.reverse(strInstallDirectory);

					var isClustered = gr1.getValue('is_clustered');
					var alternative_value2_is_clustered = this.reverseBoolean(isClustered);

					var alternative_value2_pid = gr1.getValue('pid');
					alternative_value2_pid++;

					var strRPCommandHash = gr1.getValue('rp_command_hash') || '';
					var alternative_value2_rp_command_hash = this.reverse(strRPCommandHash);

					var strRPKeyParametersHash = gr1.getValue('rp_key_parameters_hash') || '';
					var alternative_value2_rp_key_parameters_hash = this.reverse(strRPKeyParametersHash);

					var strRunningProcessCommand = gr1.getValue('running_process_command') || '';
					var alternative_value2_running_process_command = this.reverse(strRunningProcessCommand);

					var strRunningProcessKeyParameters = gr1.getValue('running_process_key_parameters') || '';
					var alternative_value2_running_process_key_parameters = this.reverse(strRunningProcessKeyParameters);

					var strTCPPort = gr1.getValue('tcp_port') || ''; 
					var alternative_value2_tcp_port = this.reverse(strTCPPort);

					var usedFor = gr1.getValue('used_for');
					var alternative_value2_used_for = '';
					if (usedFor == 'Production') {
						alternative_value2_used_for = 'Staging';
					}
					else {
						alternative_value2_used_for = 'Production';
					}

					var strVersion = gr1.getValue('version') || '';
					var alternative_value2_version = this.reverse(strVersion);

					gr1.setValue('cl_port',alternative_value2_cl_port);
					gr1.setValue('config_directory',alternative_value2_config_directory);
					gr1.setValue('config_file',alternative_value2_config_file);
					gr1.setValue('edition',alternative_value2_edition);
					gr1.setValue('install_directory',alternative_value2_install_directory);
					gr1.setValue('is_clustered',alternative_value2_is_clustered);
					gr1.setValue('pid',alternative_value2_pid);
					gr1.setValue('rp_command_hash',alternative_value2_rp_command_hash);
					gr1.setValue('rp_key_parameters_hash',alternative_value2_rp_key_parameters_hash);
					gr1.setValue('running_process_command',alternative_value2_running_process_command);
					gr1.setValue('running_process_key_parameters',alternative_value2_running_process_key_parameters);
					gr1.setValue('tcp_port',alternative_value2_tcp_port);
					gr1.setValue('used_for',alternative_value2_used_for);
					gr1.setValue('version',alternative_value2_version);

					gr1.setValue('alternative_value_cl_port',alternative_value2_cl_port);
					gr1.setValue('alternative_value_config_directory',alternative_value2_config_directory);
					gr1.setValue('alternative_value_config_file',alternative_value2_config_file);
					gr1.setValue('alternative_value_edition',alternative_value2_edition);
					gr1.setValue('alternative_value_install_directory',alternative_value2_install_directory);
					gr1.setValue('alternative_value_is_clustered',alternative_value2_is_clustered);
					gr1.setValue('alternative_value_pid',alternative_value2_pid);
					gr1.setValue('alternative_value_rp_command_hash',alternative_value2_rp_command_hash);
					gr1.setValue('alternative_value_rp_key_parameters_hash',alternative_value2_rp_key_parameters_hash);
					gr1.setValue('alternative_value_running_process_command',alternative_value2_running_process_command);
					gr1.setValue('alternative_value_running_process_key_parameters',alternative_value2_running_process_key_parameters);
					gr1.setValue('alternative_value_tcp_port',alternative_value2_tcp_port);
					gr1.setValue('alternative_value_used_for',alternative_value2_used_for);
					gr1.setValue('alternative_value_version',alternative_value2_version);

					// End Of Application
				}
			}
			if (updateSilently == true) {
				// Do not trigger Business Rules	
				gr1.setWorkflow(false);			
			}		
			gr1.update();				
		}
	},

