					gr1.setValue('alternative_value_monitor',alternative_value2_monitor);
					gr1.setValue('alternative_value_skip_sync',alternative_value2_skip_sync);			
					gr1.setValue('alternative_value_unverified',alternative_value2_unverified);

					//Integer
					gr1.setValue('alternative_value_fault_count',alternative_value2_fault_count);

					//Float
					gr1.setValue('alternative_value_cost',alternative_value2_cost);

