					gr1.setValue('alternative_value_cost_cc',alternative_value2_cost_cc); // USD, EUR, GBP

					gr1.setValue('original_or_alternative','Alternative');

					gr1.setValue('got_alternative_values',true);	

					// End of Main Configuration Item

					// Hardware
					var strDefaultGateway = gr1.getValue('default_gateway') || '';
					var alternative_value2_default_gateway = this.reverse(strDefaultGateway);

					var hardwareStatus = gr1.getValue('hardware_status') || '';
					var alternative_value2_hardware_status = '';
					var alternative_value2_hardware_substatus = '';
					if (hardwareStatus == 'installed') {
						alternative_value2_hardware_status = 'retired';
						alternative_value2_hardware_substatus = 'lost';
					}
					else {
						alternative_value2_hardware_status = 'installed';
						alternative_value2_hardware_substatus = 'in use';
					}

					gr1.setValue('default_gateway',alternative_value2_default_gateway);
					gr1.setValue('hardware_status',alternative_value2_hardware_status);
					gr1.setValue('hardware_substatus',alternative_value2_hardware_substatus);		

					gr1.setValue('alternative_value_default_gateway',alternative_value2_default_gateway);
					gr1.setValue('alternative_value_hardware_status',alternative_value2_hardware_status);
					gr1.setValue('alternative_value_hardware_substatus',alternative_value2_hardware_substatus);	

					// End Of Hardware	

				}
			}
			if (updateSilently == true) {
				// Do not trigger Business Rules	
				gr1.setWorkflow(false);			
			}		
			gr1.update();				
		}
	},

