					var cdRom = gr1.getValue('cd_rom');
					var alternative_value2_cd_rom = this.reverseBoolean(cdRom);

					var alternative_value2_cd_speed = gr1.getValue('cd_speed') || 0;
					alternative_value2_cd_speed++;

					var chassisType = gr1.getValue('chassis_type') || '';
					var alternative_value2_chassis_type = '';
					if (chassisType == 'Desktop') {
						alternative_value2_chassis_type = 'Laptop';
					}
					else {
						alternative_value2_chassis_type = 'Desktop';
					}

					var alternative_value2_cpu_core_count = gr1.getValue('cpu_core_count') || 0;
					alternative_value2_cpu_core_count++;

					var alternative_value2_cpu_core_thread = gr1.getValue('cpu_core_thread') || 0;
					alternative_value2_cpu_core_thread++;

					var alternative_value2_cpu_count = gr1.getValue('cpu_count') || 0;
					alternative_value2_cpu_count++;

					var cpuManufacturerSysID = gr1.getValue('cpu_manufacturer');
					var alternative_value2_cpu_manufacturer = '';			
					if (cpuManufacturerSysID == cmpSysID1) {
						alternative_value2_cpu_manufacturer = cmpSysID2;
					}
					else {
						alternative_value2_cpu_manufacturer = cmpSysID1;			
					}	

					var strCPUName = gr1.getValue('cpu_name') || '';
					var alternative_value2_cpu_name = this.reverse(strCPUName);

					var alternative_value2_cpu_speed = gr1.getValue('cpu_speed') || 0;
					alternative_value2_cpu_speed++;

					var strCPUType = gr1.getValue('cpu_type') || '';
					var alternative_value2_cpu_type = this.reverse(strCPUType);

					var alternative_value2_disk_space = gr1.getValue('disk_space') || 0;
					alternative_value2_disk_space++;

					var floppy = gr1.getValue('floppy') || '';
					var alternative_value2_floppy = '';
					if (floppy == '3.5 inch') {
						alternative_value2_floppy = '5.25 inch';
					}
					else {
						alternative_value2_floppy = '3.5 inch';
					}

					var formFactor = gr1.getValue('form_factor') || '';
					var alternative_value2_form_factor = '';
					if (formFactor == 'Blade') {
						alternative_value2_form_factor = 'Desktop';
					}
					else {
						alternative_value2_form_factor = 'Blade';
					}

					var strObjectID = gr1.getValue('object_id') || '';
					var alternative_value2_object_id = this.reverse(strObjectID);

					var os = gr1.getValue('os') || '';
					var alternative_value2_os = '';
					if (os == 'Windows') {
						alternative_value2_os = 'AIX';
					}
					else {
						alternative_value2_os = 'Windows';
					}

					var osAddressWidth = gr1.getValue('os_address_width') || 0;
					var alternative_value2_os_address_width = '';
					if (osAddressWidth == 32) {
						alternative_value2_os_address_width = 64;
					}
					else {
						alternative_value2_os_address_width = 32;
					}

					var strOSDomain = gr1.getValue('os_domain') || '';
					var alternative_value2_os_domain = this.reverse(strOSDomain);

					var strOSServicePack = gr1.getValue('os_service_pack') || '';
					var alternative_value2_os_service_pack = this.reverse(strOSServicePack);

					var strOSVersion = gr1.getValue('os_version') || '';
					var alternative_value2_os_version = this.reverse(strOSVersion);

					var alternative_value2_ram = gr1.getValue('ram') || 0;
					alternative_value2_ram++;

					var virtual = gr1.getValue('virtual');
					var alternative_value2_virtual = this.reverseBoolean(virtual);

					gr1.setValue('alternative_value_cd_rom',alternative_value2_cd_rom);
					gr1.setValue('alternative_value_cd_speed',alternative_value2_cd_speed);
					gr1.setValue('alternative_value_chassis_type',alternative_value2_chassis_type);
					gr1.setValue('alternative_value_cpu_core_count',alternative_value2_cpu_core_count);
					gr1.setValue('alternative_value_cpu_core_thread',alternative_value2_cpu_core_thread);
					gr1.setValue('alternative_value_cpu_count',alternative_value2_cpu_count);
					gr1.setValue('alternative_value_cpu_manufacturer',alternative_value2_cpu_manufacturer);
					gr1.setValue('alternative_value_cpu_name',alternative_value2_cpu_name);
					gr1.setValue('alternative_value_cpu_speed',alternative_value2_cpu_speed);
					gr1.setValue('alternative_value_cpu_type',alternative_value2_cpu_type);
					gr1.setValue('alternative_value_disk_space',alternative_value2_disk_space);
					gr1.setValue('alternative_value_floppy',alternative_value2_floppy);
					gr1.setValue('alternative_value_form_factor',alternative_value2_form_factor);
					gr1.setValue('alternative_value_object_id',alternative_value2_object_id);
					gr1.setValue('alternative_value_os',alternative_value2_os);
					gr1.setValue('alternative_value_os_address_width',alternative_value2_os_address_width);
					gr1.setValue('alternative_value_os_domain',alternative_value2_os_domain);
					gr1.setValue('alternative_value_os_service_pack',alternative_value2_os_service_pack);
					gr1.setValue('alternative_value_os_version',alternative_value2_os_version);
					gr1.setValue('alternative_value_ram',alternative_value2_ram);
					gr1.setValue('alternative_value_virtual',alternative_value2_virtual);				
					//End Of Computer

					//Server				
					var classification = gr1.getValue('classification') || '';
					var alternative_value2_classification = '';
					if (classification == 'Production') {
						alternative_value2_classification = 'Development';
					}
					else {
						alternative_value2_classification = 'Production';
					}

					//dr_backup	Reference	Server		32	
					//var alternative_value_dr_backup = gr1.getValue('alternative_value_dr_backup');

					var firewallStatus = gr1.getValue('firewall_status') || '';
					var alternative_value2_firewall_status = '';
					if (firewallStatus == 'Intranet') {
						alternative_value2_firewall_status = 'Internet';
					}
					else {
						alternative_value2_firewall_status = 'Intranet';
					}

					var strHostName = gr1.getValue('host_name') || '';
					var alternative_value2_host_name = this.reverse(strHostName);

					var usedFor = gr1.getValue('used_for') || '';
					var alternative_value2_used_for = '';
					if (usedFor == 'Production') {
						alternative_value2_used_for = 'Staging';
					}
					else {
						alternative_value2_used_for = 'Production';
					}

					gr1.setValue('classification',alternative_value2_classification);
					//gr1.setValue('dr_backup',alternative_value_dr_backup);
					gr1.setValue('firewall_status',alternative_value2_firewall_status);
					gr1.setValue('host_name',alternative_value2_host_name);		
					gr1.setValue('used_for',alternative_value2_used_for);

					gr1.setValue('alternative_value_classification',alternative_value2_classification);
					//gr1.setValue('dr_backup',alternative_value_dr_backup);
					gr1.setValue('alternative_value_firewall_status',alternative_value2_firewall_status);
					gr1.setValue('alternative_value_host_name',alternative_value2_host_name);		
					gr1.setValue('alternative_value_used_for',alternative_value2_used_for);
					//End Of Server				
				}
			}
			if (updateSilently == true) {
				// Do not trigger Business Rules	
				gr1.setWorkflow(false);			
			}		
			gr1.update();				
		}	
	},

	reverse: function(str){
		if (str.length > 0) {
			return str.split("").reverse().join("");		
		}
		else {
			return "";
		}
	},

	reverseBoolean: function(tf){
		if (tf == true) {
			return false;
		}
		else {
			return true;
		}
	},

    type: 'UpdateOfTestCMDBCIs'
};

