sys_script_include.script:
var UpdateConfigurationChecks = Class.create();
UpdateConfigurationChecks.prototype = {
    initialize: function() {
    },

	setDTLastRunAt: function(fld) {
		var now = new GlideDateTime();

		var checkTable = 'x_tekso_twxapp01_configuration_checks';

		//'scheduled_job_last_ran_at'
		var gr1 = new GlideRecord(checkTable);
		gr1.query();
		if (gr1.next()){
			gr1.setValue(fld,now);
			gr1.setValue('updated_by_script','SI UpdateConfigurationChecks'); // Debug	
			gr1.update();
		}
		else {
			var gr2 = new GlideRecord(checkTable);
			gr2.initialize();
			gr2.setValue(fld,now);
			gr2.setValue('updated_by_script','SI UpdateConfigurationChecks'); // Debug				
			gr2.insert();	
		}

	},
    type: 'UpdateConfigurationChecks'
};

// Called like this:
// var si = new x_tekso_twxapp01.UpdateConfigurationChecks();
// si.setDTLastRunAt('scheduled_job_last_ran_at');
