	// Queue tables do not have number fields	
	deleteQueueRecord: function(daysAgo,tbl,ref1Tbl,ref1Field) {
		//Only delete if processed = true
		var script = 'deleteQueueRecord';			
		var daysAgo2 = parseInt(daysAgo);

		var startedAt = new GlideDateTime();		
		var cnt = 0;
		var firstSysID = '';
		var firstDT = new GlideDateTime();
		var lastSysID = '';
		var lastDT = new GlideDateTime();

		if (daysAgo2 > -1) {

			// Get the most recent 100 records
			var sysIDArr = [];
			var gr1 = new GlideRecordSecure(tbl);
			gr1.orderByDesc('sys_created_on'); // Queue tables do not have number fields	
			gr1.setLimit(100);
			gr1.query();
			while(gr1.next()) {
				var sys_id = gr1.getValue('sys_id') || '';
				var sys_id2 = sys_id.toString();
				if (sys_id2.length > 0) {
					sysIDArr.push(sys_id2);
				}
			}

			var gr2 = new GlideRecordSecure(tbl);
			gr2.addQuery('sys_created_on','<',gs.daysAgo(daysAgo2));
			gr2.addQuery('processed',true);
			gr2.orderBy('sys_created_on'); //Ascending Order
			gr2.query();
			while(gr2.next()) {
				var del_sys_id = gr2.getValue('sys_id') || '';
				var del_sys_id2 = del_sys_id.toString();
				if (sysIDArr.indexOf(del_sys_id2) == -1) {
					//Not in the last 100 sys_ids

					//Ref check 1
					var ref1val = gr2.getValue(ref1Field) || '';
					var ref1val2 = ref1val.toString();
					var got_ref1 = false;
					if (ref1val2.length > 0) {
						var chk1 = this.checkForSysID(ref1Tbl,ref1val2);
						if (chk1 == true) {
							got_ref1 = true;
						}
					}

					if (got_ref1 == false) {

						if (cnt == 0) {
							firstSysID = del_sys_id2;
							firstDT = gr2.getValue('sys_created_on');
						}		
						lastDT = gr2.getValue('sys_created_on');
						lastSysID = del_sys_id2;
						cnt++;
						gr2.setWorkflow(false);
						gr2.deleteRecord();
					}
				}
			}
		}

		var finishedAt = new GlideDateTime();			
		var msg = '';
		if (cnt > 0) {
			msg = 'Deleted ' + cnt.toString() + ' records from table: ' + tbl; 
			msg = msg + ' created before: ' + gs.daysAgo(daysAgo2) + ' ranging from: ' + firstSysID; 
			msg = msg + ' created at ' + firstDT + ' to: ' + lastSysID + ' created at ' + lastDT;
			msg = msg + ' started at: ' + startedAt + ' finished at: ' + finishedAt;
		}
		else {
			msg = 'No records deleted from table: ' + tbl + ' started at: ' + startedAt + ' finished at: ' + finishedAt;
		}
		//This message will be output if property x_tekso_twxapp01.logging.level is 3 or higher		
		new x_tekso_twxapp01.DebugLogging().message(script,3,msg);	
	},

	// same as deleteQueueRecord except this has 2 references
	// Queue tables do not have number fields
	deleteQueueRecord2: function(daysAgo,tbl,ref1Tbl,ref1Field,ref2Tbl,ref2Field) {
		//Only delete if processed = true
		var script = 'deleteQueueRecord';			
		var daysAgo2 = parseInt(daysAgo);

		var startedAt = new GlideDateTime();		
		var cnt = 0;
		var firstSysID = '';
		var firstDT = new GlideDateTime();
		var lastSysID = '';
		var lastDT = new GlideDateTime();

		if (daysAgo2 > -1) {

			// Get the most recent 100 records
			var sysIDArr = [];
			var gr1 = new GlideRecordSecure(tbl);
			gr1.orderByDesc('sys_created_on'); // Queue tables do not have number fields	
			gr1.setLimit(100);
			gr1.query();
			while(gr1.next()) {
				var sys_id = gr1.getValue('sys_id') || '';
				var sys_id2 = sys_id.toString();
				if (sys_id2.length > 0) {
					sysIDArr.push(sys_id2);
				}
			}

			var gr2 = new GlideRecordSecure(tbl);
			gr2.addQuery('sys_created_on','<',gs.daysAgo(daysAgo2));
			gr2.addQuery('processed',true);
			gr2.orderBy('sys_created_on'); //Ascending Order
			gr2.query();
			while(gr2.next()) {
				var del_sys_id = gr2.getValue('sys_id') || '';
				var del_sys_id2 = del_sys_id.toString();
				if (sysIDArr.indexOf(del_sys_id2) == -1) {
					//Not in the last 100 sys_ids

					//Ref check1
					var ref1val = gr2.getValue(ref1Field) || '';
					var ref1val2 = ref1val.toString();
					var got_ref1 = false;
					if (ref1val2.length > 0) {
						var chk1 = this.checkForSysID(ref1Tbl,ref1val2);
						if (chk1 == true) {
							got_ref1 = true;
						}
					}

					//Ref check2
					var ref2val = gr2.getValue(ref2Field) || '';
					var ref2val2 = ref2val.toString();
					var got_ref2 = false;
					if (ref1val2.length > 0) {
						var chk2 = this.checkForSysID(ref2Tbl,ref2val2);
						if (chk2 == true) {
							got_ref2 = true;
						}
					}

					if (got_ref1 == false && got_ref2 == false) {

						if (cnt == 0) {
							firstSysID = del_sys_id2;
							firstDT = gr2.getValue('sys_created_on');
						}		
						lastDT = gr2.getValue('sys_created_on');
						lastSysID = del_sys_id2;
						cnt++;
						gr2.setWorkflow(false);
						gr2.deleteRecord();
					}
				}
			}
		}

		var finishedAt = new GlideDateTime();			
		var msg = '';
		if (cnt > 0) {
			msg = 'Deleted ' + cnt.toString() + ' records from table: ' + tbl; 
			msg = msg + ' created before: ' + gs.daysAgo(daysAgo2) + ' ranging from: ' + firstSysID; 
			msg = msg + ' created at ' + firstDT + ' to: ' + lastSysID + ' created at ' + lastDT;		
			msg = msg + ' started at: ' + startedAt + ' finished at: ' + finishedAt;			
		}
		else {
			msg = 'No records deleted from table: ' + tbl + ' started at: ' + startedAt + ' finished at: ' + finishedAt;
		}
		//This message will be output if property x_tekso_twxapp01.logging.level is 3 or higher		
		new x_tekso_twxapp01.DebugLogging().message(script,3,msg);	
	},	

	deleteEmptyScheduledJobRecords: function(daysAgo) {

		var script = 'deleteEmptyScheduledJobRecords';
		var tbl = 'x_tekso_twxapp01_scheduled_job_log';
		var tbl2 = 'x_tekso_twxapp01_processing_log';	
		var daysAgo2 = parseInt(daysAgo);

		var startedAt = new GlideDateTime();
		var cnt = 0;
		var firstSysID = '';
		var firstNum = '';
		var firstDT = new GlideDateTime();
		var lastSysID = '';
		var lastNum = '';
		var lastDT = new GlideDateTime();

		if (daysAgo2 > -1) {

			// Get the most recent 100 records
			var sysIDArr = [];
			var gr1 = new GlideRecordSecure(tbl);
			gr1.orderByDesc('number');
			gr1.setLimit(100);
			gr1.query();
			while(gr1.next()) {
				var sys_id = gr1.getValue('sys_id') || '';
				var sys_id2 = sys_id.toString();
				if (sys_id2.length > 0) {
					sysIDArr.push(sys_id2);
				}
			}

			var gr2 = new GlideRecordSecure(tbl);
			gr2.addQuery('data_processed',false);
			gr2.addQuery('sys_created_on','<',gs.daysAgo(daysAgo2));
			gr2.orderBy('sys_created_on'); //Ascending Order
			gr2.query();
			while(gr2.next()) {
				var del_sys_id = gr2.getValue('sys_id') || '';
				var del_sys_id2 = del_sys_id.toString();
				if (sysIDArr.indexOf(del_sys_id2) == -1) {
					//Not in the last 100 sys_ids	

					//Double check that there is no link
					var gr3 = new GlideRecordSecure(tbl2);
					gr3.addQuery('link_to_scheduled_job_log_record',del_sys_id2);
					gr3.query();
					if (!gr3.next()) {
						gr2.deleteRecord();
						if (cnt == 0) {
							firstSysID = del_sys_id2;
							firstDT = gr2.getValue('sys_created_on');
						}		
						lastDT = gr2.getValue('sys_created_on');
						lastSysID = del_sys_id2;
						cnt++;
						gr2.setWorkflow(false);
						gr2.deleteRecord();						
					}
				}
			} //while(gr2.next()) {
		} //if (daysAgo2 > -1) {

		var finishedAt = new GlideDateTime();		
		var msg = '';
		if (cnt > 0) {
			msg = 'Deleted ' + cnt.toString() + ' records from table: ' + tbl; 
			msg = msg + ' created before: ' + gs.daysAgo(daysAgo2) + ' ranging from: ' + firstNum; 
			msg = msg + ' created at ' + firstDT + ' to: ' + lastNum + ' created at ' + lastDT;		
			msg = msg + ' started at: ' + startedAt + ' finished at: ' + finishedAt;			
		}
		else {
			msg = 'No records deleted from table: ' + tbl + ' started at: ' + startedAt + ' finished at: ' + finishedAt;
		}	

		//This message will be output if property x_tekso_twxapp01.logging.level is 3 or higher		
		new x_tekso_twxapp01.DebugLogging().message(script,3,msg);	
	},

