sys_security_acl.script:
//Only allow creation if the user has the role x_tekso_twxapp01.admin
//and no records exist
//i.e. if a record already exists don't allow the creation of another
answer = false;
if(gs.hasRole('x_tekso_twxapp01.admin') || gs.hasRole('admin')) {
  var gr = new GlideRecord("x_tekso_twxapp01_setup");
  gr.query();
  var cn = gr.getRowCount();
  if(cn>=1) {
    answer = false;
  }
  else {
    answer = true;
  }
}
