sys_ui_page.client_script:
function onAbort() {
	//var sys_id = gel("sysID").value;
	//var now = gel("now").value;

	GlideDialogWindow.get().destroy();
	return true;
}

sys_ui_page.processing_script:
var gr1 = new GlideRecord('x_tekso_twxapp01_setup');
gr1.addQuery('sys_id',sysID);
gr1.query();
if (gr1.next()) {
	gr1.setValue('connection_status','Failed');				
	gr1.setValue('connection_status_details','Connection Test Aborted');
	gr1.setValue('start_sending_requested',false);
	gr1.setValue('connection_test_requested',false);	
	gr1.update();
	var url = '/x_tekso_twxapp01_setup.do?sys_id=' + sysID;
	response.sendRedirect(url);	
}
