sysauto_script.script:
main();

function main() {
	// --Start ------------------------------------------------------------
	var script = 'SJ Send Data to uControl';
	//This message will be output if property x_tekso_twxapp01.debug is 1 or higher	
	new x_tekso_twxapp01.DebugLogging().message(script,1,'SJ Send Data to uControl - Started');

	var calculatedNextJobStartTime = new GlideDateTime();

	var jobTable = 'x_tekso_twxapp01_scheduled_job_log';
	var setupTable = 'x_tekso_twxapp01_setup';
	var setupSysID = '';
	var setupSysID2 = '';
	var setupStatus = '';
	var setupStatusWasSendingWhenConnectionFailed = false;
	var setupURL = '';
	var setupMIDSrvrSysID = '';
	var setupMIDSrvrSysID2 = '';
	var setupMIDSrvrName = '';
	var setupMIDSrvrName2 = '';	
	var setupConnTimeoutMins = 0;
	var gotSetupRecord = false;
	var connectionTestRequested = false;

	// --Work out when the scheduled job is going to run next -----------------
	// Number of seconds till the next run of the job
	var gdt1 = new GlideDateTime('1970-01-01 00:00:00');
	var gdt2 = new GlideDateTime(current.run_period); //Need to convert current.run_period into a GlideDateTime
	var jobIntervalDuration = GlideDateTime.subtract(gdt1, gdt2);
	var jobIntervalDurationMS = jobIntervalDuration.getNumericValue();
	var jobIntervalDurationSeconds = parseInt(jobIntervalDurationMS/1000);

	// Two ways of calculating the next time
	// 1: Get the current time and add on the run_period
	// 2: Get the next_action from sys_trigger -- unfortunately this won't work because it is 
	//    updated after the job ends
	//    The time retrieved is the time of the current run
	calculatedNextJobStartTime.addSeconds(jobIntervalDurationSeconds);

	// --Create a new scheduled_job_log record -------------------------------
	var gr1 = new GlideRecordSecure(jobTable); //x_tekso_twxapp01_scheduled_job_log
	gr1.initialize();
	gr1.setValue('inserted_by_script','SJ Send Data to uControl'); // Debug
	gr1.setValue('next_run_at',calculatedNextJobStartTime);	
	var jobSysID = gr1.insert();
    var jobSysID2 = jobSysID.toString();

	// --Get the setup record and update ------------------------------------
	var gr2 = new GlideRecordSecure(setupTable); //x_tekso_twxapp01_setup
	gr2.query();
	if (gr2.next()) {
		gotSetupRecord = true;
		setupSysID = gr2.getValue('sys_id');
		setupSysID2 = setupSysID.toString();

		// Get the sys_id for this script
		var sysIDForThisScript = current.sys_id;

		// Ensure that the setup table has the sys_id for this script so that if someone
		// changes the interval then this can be updated
		var setupScheduledJobSysID = gr2.getValue('scheduled_job_sys_id') || '';
		if (sysIDForThisScript != setupScheduledJobSysID) {
			gr2.setValue('scheduled_job_sys_id',sysIDForThisScript);
		}
		setupStatus = gr2.getValue('status') || '';
		setupStatusWasSendingWhenConnectionFailed = gr2.getValue('status_was_sending_when_connection_failed');

		setupConnTimeoutMins = gr2.getValue('ucontrol_connection_timeout_minutes') || 0; 
		setupURL = gr2.getValue('ucontrol_url') || ''; 

		// Need to use getElement to do dot walking.
		setupMIDSrvrSysID = gr2.getElement('ucontrol_mid_server.sys_id') || '';
		setupMIDSrvrSysID2 = setupMIDSrvrSysID.toString(); 

		setupMIDSrvrName = gr2.getElement('ucontrol_mid_server.name') || '';	
		setupMIDSrvrName2 = setupMIDSrvrName.toString();		
	}
	else {
		// Could not get gr2 record -- i.e. setup record could not be found
		gr1.setValue('setup_status','No Setup Record');
		gr1.setValue('updated_by_script','SJ Send Data to uControl');
		gr1.update();
	}

	// --Main processing ----------------------------------------------------
	if (setupStatus == 'Sending') {
		// Trigger an event to do the background processing -- seems to be about a 10 sec delay
		// this will trigger a script action
		gs.eventQueue('x_tekso_twxapp01.senddatatoucontrol', gr1, jobSysID2, calculatedNextJobStartTime);
	} // if (setupStatus == 'Sending')
	else {
		gr1.setValue('setup_status',setupStatus);
		gr1.setValue('updated_by_script',script);
		gr1.update();		
	} // if (setupStatus == 'Sending')

	// --Finished ------------------------------------------------------------
	if (gotSetupRecord == true) {
		// Check the last time a connection test was performed
		var setupConnTestedAt = gr2.getValue('connection_tested_at');
		var now = new GlideDateTime();
		//Need to put this through GlideDateTime()
		var lastTestedAt = new GlideDateTime(setupConnTestedAt);
		var dur = new GlideDuration();
		dur = GlideDateTime.subtract(lastTestedAt,now);	// In Milliseconds	

		// Seconds - will be floating point e.g. 4,152.345
		//var elaspsedTimeSinceConnectionTestInSecs = dur.getNumericValue() / 1000; 

		// Minutes - will be floating point e.g. 69.206
		var elapsedTimeSinceConnectionTestInMins = dur.getNumericValue() / 1000 / 60;  
		var elapsedTimeSinceConnectionTestInMins2 = parseInt(elapsedTimeSinceConnectionTestInMins); // e.g. 69
		gr1.setValue('time_elapsed_since_last_connection_test',dur);		
		gr1.setValue('connection_tested_at',setupConnTestedAt);
		if (elapsedTimeSinceConnectionTestInMins2 > 30) {
			//Last Connection Test was more than 30 minutes ago
			if (setupStatus == 'Sending') {
				var noOfPendingRecs = getNumberOfAllPendingRecords();	
				if (noOfPendingRecs == 0) {
					// Status is Sending and there is nothing in the queue - Do a connection test
					connectionTestRequested = true;
					gr1.setValue('connection_test_requested_by_job',true);
				}
			}
			else {
				if (setupStatusWasSendingWhenConnectionFailed == true) {
					connectionTestRequested = true;
					gr1.setValue('connection_test_requested_by_job',true);
				}				
			}
		}
	}

	var finishedAt = GlideDateTime();
	gr1.setValue('finished_at',finishedAt);
	gr1.setValue('updated_by_script','SJ Send Data to uControl');
	gr1.update();

	if (connectionTestRequested == true) {				
		var trggrdBy = 'SJ Send Data to uControl';
		var usrNm = 'Scheduled Job';

		var si = new x_tekso_twxapp01.ConnectionTest();
		si.buildRequestRecord(setupSysID2,setupURL,setupMIDSrvrName2,setupMIDSrvrSysID2,usrNm,true,trggrdBy,setupConnTimeoutMins);

	}

	//This message will be output if property x_tekso_twxapp01.debug is 1 or higher	
	new x_tekso_twxapp01.DebugLogging().message(script,1,'SJ Send Data to uControl - Finished');	
}


function getNumberOfAllPendingRecords() {
	// Get the number of all Pending Records - this is used to determine if the queue is empty
	var trackingTable = 'x_tekso_twxapp01_cmdb_changes';
	var rtnCnt = 0;
	var gr1 = new GlideAggregate(trackingTable); //x_tekso_twxapp01_cmdb_changes
	gr1.addQuery('status','Pending');
	gr1.addQuery('processing_id','');	
	gr1.addAggregate('COUNT');	
	gr1.query();
	gr1.next(); // GlideAggregate needs the .next()
	rtnCnt = gr1.getAggregate('COUNT');	
	return rtnCnt;
}
