function secs_to_hhmmss(seconds) {
	// Convert seconds to hh:mm:ss, e.g. 71 = 00:01:11	
	var hrs = Math.floor(seconds / 3600);
	var mins = Math.floor((seconds - (hrs * 3600)) / 60);
	var secs = seconds - (hrs * 3600) - (mins * 60);
	secs2 = Math.round(secs * 100) / 100;

	var hrsStr = '00' + hrs.toString();
	var minsStr = '00' + mins.toString();
	var secsStr = '00' + secs2.toString();

	var hrsStr2 = hrsStr.substring(hrsStr.length - 2, hrsStr.length);
	var minsStr2 = minsStr.substring(minsStr.length - 2, minsStr.length);
	var secsStr2 = secsStr.substring(secsStr.length - 2, secsStr.length);

	var dur = hrsStr2 + ':' + minsStr2 + ':' + secsStr2;
	return dur;
}
