sysauto_script.script:
writeDebugLog('Set All Records To Pending - Start');

var gr3 = new GlideRecord('x_tekso_twxapp01_cmdb_changes');
gr3.addQuery('status','!=','Pending');
gr3.query();
while(gr3.next()) {
	gr3.setValue('status','Pending');
	gr3.update();
}

writeDebugLog('Set All Records To Pending - End');

function writeDebugLog(msg) {
	var debugTable = 'x_tekso_twxapp01_debug_log';
	var gr1 = new GlideRecord(debugTable);
	gr1.initialize();
	gr1.setValue('message',msg);
	gr1.insert();
}
