sysevent_script_action.script:
// Event x_tekso_twxapp01.testconnectiontimeout is triggered by BR - Connection Test - Insert

TestConnectionTimeOutScriptAction();

function TestConnectionTimeOutScriptAction() {
	gs.info('TestConnectionTimeOutScriptAction - Triggered');
	//Note: parm not param
	//gs.info('event.parm1: ' + event.parm1); // gs.getUserID()
	//gs.info('event.parm2: ' + event.parm2); // gs.getUserName()

	var setupTable = 'x_tekso_twxapp01_setup';
	var connectionTestTable = 'x_tekso_twxapp01_connection_test';

	// current should be the x_tekso_twxapp01_connection_test record
	var sysID = current.sys_id;
	// Get the connection test record
	var gr1 = new GlideRecord(connectionTestTable);
	gr1.addQuery('sys_id',sysID);
	gr1.query();
	if (gr1.next()) {
		// Got the connection test record
		var status = gr1.getValue('status') || '';
		var setupRecord = gr1.getValue('setup_record') || '';
		var timedOutStatus = gr1.getValue('timed_out') || false;
		var abortedStatus = gr1.getValue('aborted') || false;		

		// This may have already been aborted or timed out

		if (abortedStatus == true && status != 'Aborted') {
			// Because of timing between various async threads the status may not have been set to Aborted
			// or it was set to Aborted but then overwritten by another process
			// Hence the need for a separated Aborted flag
			// If Status is not Aborted - set it now			
			gr1.setValue('status','Aborted');	
			gr1.update();
		}

		if (timedOutStatus == true && status != 'Timed Out') {
			// Because of timing between various async threads the status may not have been set to Timed Out
			// or it was set to Timed Out but then overwritten by another process
			// Hence the need for a separated Timed Out flag
			// If Status is not Timed Out - set it now			
			gr1.setValue('status','Timed Out');	
			gr1.update();
		}

		if (abortedStatus == false && timedOutStatus == false) {
			// Not alredy aborted
			if (status == 'Pending' || status == 'Sent') {		
				gr1.setValue('status','Timed Out');
				gr1.setValue('timed_out',true);
				gr1.update();
				var gr2 = new GlideRecord(setupTable);
				gr2.addQuery('sys_id',setupRecord);
				gr2.query();
				if (gr2.next()) {
					var gdt = new GlideDateTime();
					gr2.setValue('connection_status','Failed');				
					gr2.setValue('connection_status_details','Connection Test Timed Out At: ' + gdt); // .getDisplayValue()
					gr2.setValue('start_sending_requested',false);
					gr2.setValue('connection_test_requested',false);
					gr2.setValue('connection_test_link','');
					gr2.update();
				} // if (gr2.next())
			} // if (status == 'Pending' || status == 'Sent')
		} // if (abortedStatus == false && timedOutStatus == false)
	} // if (gr1.next()) 
}
