function cleanup() {
	//Note: parm not param
	//gs DOT info('event.parm1: ' + event.parm1); // gs.getUserID()
	//gs DOT info('event.parm2: ' + event.parm2); 	

	var script = 'SA Cleanup';
	//This message will be output if property x_tekso_twxapp01.debug is 2 or higher	
	new x_tekso_twxapp01.DebugLogging().message(script,2,'SA Cleanup - Started');

	//Clean up any old ConnectionTests
	var si = new x_tekso_twxapp01.ConnectionTestTimeout();
	si.cleanup();

	//This message will be output if property x_tekso_twxapp01.debug is 2 or higher	
	new x_tekso_twxapp01.DebugLogging().message(script,2,'SA Cleanup - Finished');

}
